INSERT INTO `pageset` VALUES (3, 1, '关于我们－位置地图', '', '位置地图－新沙珑国际宾馆', '', '', '<div>\r\n	<strong>从浦东机场到新沙珑国际宾馆</strong></div>\r\n<div>\r\n	如果您在浦东国际机场，通过以下路线到新沙珑国际宾馆。</div>\r\n<div>\r\n	&nbsp;1、由浦东国际机场：A30高速&rarr;A5高速&rarr;A8高速&rarr;右转嘉松公路。（约1小时） &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</div>\r\n<div>\r\n	&nbsp;2、从浦东机场乘磁悬浮 &rarr;龙阳路站转2号线(龙阳路&rarr;中山公园)&rarr;转3号线(中山公园&rarr;宜山路)&rarr;转9号线(宜山路&rarr;松江大学城) （约2小时30分） &nbsp; &nbsp;</div>\r\n<div>\r\n	　</div>\r\n<div>\r\n	<strong>从虹桥机场到新沙珑国际宾馆</strong></div>\r\n<div>\r\n	如果您在虹桥机场，通过以下路线到新沙珑国际宾馆</div>\r\n<div>\r\n	1、由虹桥国际机场：延安高架&rarr;A9高速&rarr;嘉松公路往南行驶（30分钟） &nbsp; &nbsp;&nbsp;</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong>大众交通</strong></div>\r\n<div>\r\n	地铁</div>\r\n<div>\r\n	新沙珑酒店在地铁9号线松江大学城站旁，您可以方便的利用地铁到达</div>\r\n<div>\r\n	由市中心区 ：到松江大学城站下<br />\r\n	一般公共汽车</div>\r\n<div>\r\n	松江地区3路、5路、18路，梅家浜路站下</div>\r\n<div>\r\n	　</div>\r\n<div>\r\n	<strong>自行驾车</strong></div>\r\n<div>\r\n	1、由南浦大桥经内环中山南路到莘庄接A8高速（约45分钟）</div>\r\n<div>\r\n	2、由闵行莘庄：A8高速 松江新城出口，直行到梅家浜路左转（约25分钟）</div>\r\n<div>\r\n	3、由佘山：嘉松公路驱车往南行驶（约10分钟）</div>\r\n<div>\r\n	4、由松江工业区：往大学城方向驱车 行驶（约15分钟）</div>\r\n<div>\r\n	5、由宾馆至著名旅游景点泰晤士小镇仅10分钟车程</div>\r\n<div>\r\n	6、由宾馆至佘山欢乐谷车程15分钟</div>\r\n', 3);
