INSERT INTO `pageset` VALUES (15, 0, '日文－关于我们－位置地图', '', '位置地图－新沙珑国际宾馆', '', '', '<div>\r\n	<strong>浦東空港から国際賓館へ</strong></div>\r\n<div>\r\n	1、浦東国際空港から：A30高速 &rarr;A5高速&rarr; A8高速&rarr; 右へ回り、嘉松道路へ(約1時間)</div>\r\n<div>\r\n	2、リニアモーターカー＋地下鉄からのアクセス</div>\r\n<div>\r\n	リニアモーターカー：浦東国際空港&rarr;「龙阳路駅」下車 地下鉄：地下鉄2号線（龙阳路駅～中山公園駅）&rarr;地下鉄3号線（中山公園駅～宜山路駅）&rarr;地下鉄9号線（宜山路駅～松江大学城駅）下車(約2時間30分) なお「宜山路駅」から「桂林路駅」まで無料シャトルバスをご利用いただけます、改札を出ることなくシャトルバスにご乗車下さい</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong>虹橋空港から国際賓館へ</strong></div>\r\n<div>\r\n	1、虹橋空港から：延安高架&rarr; A9道路&rarr; 嘉松道路を南へ進みます（約30分 ）</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong>交通機関</strong></div>\r\n<div>\r\n	地下鉄</div>\r\n<div>\r\n	9号線「松江大学城駅」より徒歩5分</div>\r\n<div>\r\n	バス</div>\r\n<div>\r\n	松江地区3路、5路、18路、梅家浜路駅</div>\r\n<div>\r\n	　</div>\r\n<div>\r\n	<strong>お車をご利用の場合</strong></div>\r\n<div>\r\n	1、南浦大橋より内環中山南路を経由し、莘荘までA8高速（約45分）</div>\r\n<div>\r\n	2、閔行莘荘よりA8高速淞江新城出口&rarr; 梅家浜路を左折（約25分）</div>\r\n<div>\r\n	3、由佘山：嘉松公路驱车往南行驶（约10分钟）</div>\r\n<div>\r\n	4、松江工業区域より約15分</div>\r\n', 15);
